SELECT o.id, o.device_code, o.device_name, o.device_pic_url, o.device_location, o.firm_id, o.brand_id, o.is_putaway, o.is_hired, o.device_desc, o.device_stock, o.device_rental, o.device_deposit, o.device_type, o.device_spec, o.create_time, o.update_time, o.isDel, o.networking_type, o.definition_id, o.device_locationId AS managerId, o.platform_id as platformId, b.`brand_name` AS brandName, f.`firm_name` AS firmName, t.`device_type_name` AS deviceTypeName, d.`definition_name` AS definitionName FROM device_info o LEFT JOIN `device_brands` b ON o.`brand_id` = b.`id` LEFT JOIN `device_firms` f ON o.`firm_id` = f.`id` LEFT JOIN `device_type` t ON o.`device_type` = t.`id` LEFT JOIN `device_definition` d ON o.`definition_id` = d.`id` WHERE 1 = 1
AND device_name like CONCAT(#{deviceName},'%')
AND device_type = #{deviceType}
AND firm_id = #{firmId}
AND brand_id = #{brandId}
AND firm_id IN (SELECT ff.`id` FROM `device_firms` ff WHERE ff.`firm_name` = #{firmName})
AND brand_id IN (SELECT bb.`id` FROM `device_brands` bb WHERE bb.`brand_name` = #{brandName})
AND is_putaway = #{isPutaway}
AND is_hired = #{isHired}
AND definition_id = #{definitionId}
AND o.platform_id = #{platformId}
order by o.`id` desc

SELECT i.* FROM device_info i
WHERE i.`device_name` = #{deviceName} 

	and i.platform_id = #{platformId}

limit 1

SELECT COUNT(*) FROM `device_info` i 
WHERE i.`firm_id` = #{firmId}

		and i.platform_id =#{platformId}

SELECT COUNT(*) FROM `device_info` i 
WHERE i.`brand_id` = #{brandId}

		and i.platform_id = #{platformId}

  	SELECT i.`id` FROM `device_info` i 
  	WHERE i.`device_code` = #{deviceCode}

			and i.platform_id = #{platformId}

  	LIMIT 1

SELECT i.* FROM device_info i 
WHERE i.`device_code` = #{deviceCode} 
AND i.`definition_id` = #{definitionId}

		and i.platform_id = #{platformId}

SELECT i.* FROM device_info i WHERE i.`device_code` = #{deviceCode} 
AND i.`definition_id` = #{definitionId} AND i.`id` != #{id}

		and i.platform_id = #{platformId}

