select pd.id, pd.manager_id, pd.name, pd.sex, pd.card_type, pd.card_num, pd.birthday, pd.mobile, pd.health_card, pd.document_id, pd.new_farmers_card, pd.medical_insurance_card, pd.residential_address, pd.permanent_address, pd.work_unit, pd.family_relations, pd.contacts_name, pd.contacts_mobile, pd.living_state, pd.living_type, pd.registered_residence, pd.arrive_date, pd.country, pd.nation, pd.blood_type, rh, pd.degree_education, pd.occupation, pd.marriage_status, pd.date_marriage, pd.document_doctor, pd.document_date, pd.medical_costs, pd.medical_card_number, pd.low_insurance_car, pd.drug_allergy_history, pd.past_history, pd.mother_medical_history, pd.father_medical_history, pd.children_medical_history, pd.brother_medical_history, pd.heredity_history, pd.is_disability, pd.disability_card, pd.expose_history, pd.operation_history, pd.trauma_history, pd.blood_transfusion_history, pd.kitchen_exhaust, pd.fuel_type, pd.water, pd.toilet, pd.livestock, pd.remarks, pd.key_crowd_type, pd.province_code, pd.city_code, pd.town_code, pd.create_time, pd.update_time, pd.is_sign,pd.variable from xl_person_document AS pd left join xl_person_contract pc on pd.id = pc.document_id and pc.in_use = 1 where 1=1
and pd.id = #{id,jdbcType=INTEGER}
and pd.is_sign = #{isSign,jdbcType=VARCHAR}
and pd.document_doctor = #{documentDoctor,jdbcType=VARCHAR}
and pd.name like CONCAT(#{name,jdbcType=VARCHAR},'%')
and pd.document_id like CONCAT(#{documentId,jdbcType=VARCHAR},'%')
and pd.card_num like CONCAT(#{cardNum,jdbcType=VARCHAR},'%')
and LOCATE(#{keyCrowdType,jdbcType=VARCHAR},key_crowd_type)>0
and pc.sign_start_date >= #{signStartTime,jdbcType=VARCHAR}
and pc.sign_start_date <= #{signEndTime,jdbcType=VARCHAR}
and pd.province_code = #{provinceCode,jdbcType=VARCHAR}
and pd.city_code = #{cityCode,jdbcType=VARCHAR}
and pd.town_code = #{townCode,jdbcType=VARCHAR}
and pd.platform_id = #{platformId,jdbcType=INTEGER}
order by pd.create_time desc

select
from xl_person_document AS pd where 1=1
and pd.id = #{id,jdbcType=INTEGER}
and pd.is_sign = #{isSign,jdbcType=VARCHAR}
and pd.platform_id = #{platformId,jdbcType=INTEGER}
and pd.name like CONCAT(#{name,jdbcType=VARCHAR},'%')
and pd.document_id like CONCAT(#{documentId,jdbcType=VARCHAR},'%')
and pd.card_num like CONCAT(#{cardNum,jdbcType=VARCHAR},'%')
and LOCATE(#{keyCrowdType,jdbcType=VARCHAR},key_crowd_type)>0
order by pd.create_time desc

SELECT d4.* FROM ( SELECT d3.* FROM (
SELECT d1.*,b2.name AS doctorName,ro.`item_name` AS roleName, b1.`name` AS parentName,b1.mobile AS parentMobile ,NULL AS diseaseIds FROM ( SELECT da.id AS contractedId, r.manager_id AS managerId, da.`name` NAME, IF (a.mobile IS NULL,da.mobile,a.mobile) AS mobile, r.doctor_id AS doctorId, r.in_use AS inUse, IF (da.sex IS NULL,a.sex,da.sex) AS sex, r.create_time AS createTime, r.build_type AS buildType, IFNULL((YEAR(NOW())-YEAR(da.birthday)-1) + ( DATE_FORMAT(da.birthday, '%m%d') <= DATE_FORMAT(NOW(), '%m%d')),-1) AS age, a.`parent_id` AS parentId, a.`role_id` AS roleId, da.key_crowd_type as keyCrowdType, da.service_package as servicePackage, da.sign_person_type as signPersonType FROM user_doctor_rel r LEFT JOIN ( SELECT c.id,d.key_crowd_type,d.`manager_id`,d.`mobile`,d.`birthday`,d.`card_num` ,d.sex,d.name,c.service_package,c.sign_person_type FROM xl_person_contract c LEFT JOIN xl_person_document d ON c.document_id = d.`id` WHERE c.`in_use` = 1 )da ON r.contracted_user_id = da.id LEFT JOIN api_user_info a ON r.manager_id = a.manager_id WHERE r.`build_type` =1 and r.in_use = 1 )d1 LEFT JOIN api_user_info b1 ON d1.parentId = b1.`manager_id` LEFT JOIN backstage_user_info b2 ON d1.doctorId = b2.manager_id LEFT JOIN api_role_info ro ON d1.roleId = ro.`id` WHERE 1=1
and d1.doctorId in
${item}
and LOCATE(#{keyCrowdType,jdbcType=VARCHAR},d1.keyCrowdType)>0
and LOCATE(#{signPersonType,jdbcType=VARCHAR},d1.signPersonType)>0
and d1.servicePackage = #{servicePackage,jdbcType=VARCHAR}
and d1.age >= #{ageBegin,jdbcType=INTEGER}
and d1.age <= #{ageEnd,jdbcType=INTEGER}
GROUP BY d1.contractedId
UNION
SELECT d1.*,bu.name AS doctorName,ar.`item_name` AS roleName, au.`name` AS parentName,au.mobile AS parentMobile , NULL AS keyCrowdType,NULL AS diseaseIds,NULL AS servicePackage,NULL AS signPersonType FROM ( SELECT r.`contracted_user_id` AS contractedId,r.`manager_id` AS managerId,u.`name` AS NAME ,a.`mobile` AS mobile,r.`doctor_id` AS doctorId,r.`in_use` AS in_use,a.`sex` AS sex, r.`create_time` AS createTime,r.`build_type` AS buildType,u.`age`,a.`parent_id` AS parentId,a.`role_id` AS roleId FROM user_doctor_rel r LEFT JOIN api_motherhood_user u ON r.`contracted_user_id` = u.`id` LEFT JOIN api_user_info a ON r.`manager_id` = a.`manager_id` WHERE r.`build_type` =3 and r.in_use = 1 )d1 LEFT JOIN api_user_info au ON d1.parentId = au.`manager_id` LEFT JOIN backstage_user_info bu ON d1.doctorId = bu.`manager_id` LEFT JOIN api_role_info ar ON d1.roleId = ar.`id` where 1=1
and d1.doctorId in
${item}
and d1.age >= #{ageBegin,jdbcType=INTEGER}
and d1.age <= #{ageEnd,jdbcType=INTEGER}
UNION
SELECT d1.*,b2.name AS doctorName,ro.`item_name` AS roleName, b1.`name` AS parentName,b1.mobile AS parentMobile, NULL AS keyCrowdType,null AS diseaseIds ,NULL AS servicePackage,NULL AS signPersonType FROM ( SELECT null AS contractedId, r.manager_id AS managerId, a.name AS NAME, a.mobile AS mobile,r.doctor_id AS doctorId, r.in_use AS inUse, a.sex AS sex, r.create_time AS createTime, r.build_type AS buildType, IFNULL((YEAR(NOW())-YEAR(a.birthday)-1) + ( DATE_FORMAT(a.birthday, '%m%d') <= DATE_FORMAT(NOW(), '%m%d')),-1) AS age, a.`parent_id` AS parentId, a.`role_id` AS roleId FROM user_doctor_rel r LEFT JOIN api_user_info a ON r.manager_id = a.manager_id where r.build_type ='2' and r.in_use = 1 )d1 LEFT JOIN api_user_info b1 ON d1.parentId = b1.`manager_id` LEFT JOIN backstage_user_info b2 ON d1.doctorId = b2.manager_id LEFT JOIN api_role_info ro ON d1.roleId = ro.`id` WHERE 1=1 AND d1.buildType ='2'
and d1.doctorId in
${item}
UNION
SELECT d2.*,ro2.`item_name` AS roleName,b3.`name` AS parentName, b3.mobile AS parentMobile , NULL AS keyCrowdType,null AS diseaseIds ,NULL AS servicePackage,NULL AS signPersonType FROM ( SELECT NULL AS contractedId,o.`user_id` AS managerId, u.name AS NAME,u.mobile AS mobile,o.`back_manager_id` AS doctorId,o.`in_use` AS inUse, u.`sex` AS sex,o.`create_time` AS createTime,4 AS buildType, -1 AS age, u.`parent_id` AS parentId,u.`role_id` AS roleId,b.name AS doctorName FROM order_info o LEFT JOIN api_user_info u ON o.`user_id` = u.`manager_id` LEFT JOIN backstage_user_info b ON o.`back_manager_id` = b.`manager_id` WHERE 1=1 AND o.back_manager_id IS NOT NULL GROUP BY o.user_id,o.back_manager_id ORDER BY o.create_time ASC )d2 LEFT JOIN api_user_info b3 ON d2.parentId = b3.`manager_id` LEFT JOIN api_role_info ro2 ON d2.roleId = ro2.`id` where 1=1
and d2.doctorId in
${item}
)d3
1=1
and d3.name like #{name,jdbcType=VARCHAR}
and d3.mobile = #{mobile,jdbcType=VARCHAR}
and (d3.buildType='1' or d3.buildType='3')
and d3.buildType='2' or d3.buildType='4'
AND DATE_FORMAT(d3.createTime, '%Y-%m-%d') >= DATE_FORMAT(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d')
and DATE_FORMAT(d3.createTime, '%Y-%m-%d') <= DATE_FORMAT(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d')
)d4 WHERE 1=1 GROUP BY d4.managerId ORDER BY d4.createTime DESC
